-- player
player = {
	sp=1,
	x=120,
	y=68,
	w=8,
	h=8,
	flp=0,
	dx=0,
	dy=0,
	max_dx=3,
	max_dy=4,
	acc=0.5,
	boost=5,
	anim=0,
	running=false,
	jumping=false,
	falling=false,
	sliding_x=false,
	sliding_y=false,
	landed=true,
	hb={xoff=2, yoff=0, w=4, h=8}
}

function player_upd()
	--physics
	player.dy = player.dy+gravity
	player.dx = player.dx*friction
	
	--controls
	if btn(2) then
		player.dx = player.dx-player.acc
		player.flp = 1
		player.running = true
	end
	if btn(3) then
		player.dx = player.dx + player.acc
		player.flp = 0
		player.running = true
	end
	
	--slide_x
	if player.running
	and not btn(2)
	and not btn(3)
	and player.landed then
		player.running=false
		player.sliding_x=true
	end
	
	--jump
	if btnp(4)
	and player.landed then
		player.dy = player.dy-player.boost
		player.landed=false
	end
	
	--check collision up/down
	if player.dy>0 then
		player.falling=true
		player.landed=false
		player.jumping=false
		
		player.dy=limit_speed(player.dy,player.max_dx)
		
		if collide_map(player,"down","top_colliders") or collide_map(player,"down","solid_colliders") then
			player.landed=true
			player.falling=false
			player.dy=0
			player.y = player.y - (((player.y+player.h+1) % 8) - 1)
			-- test --
			-- collide_d = "yes"
			-- else collide_d = "no"
			----------
		end
	elseif player.dy<0 then
		player.jumping=true
		if collide_map(player,"up","solid_colliders") then
			player.dy=0
			-- test --
			-- collide_u = "yes"
			-- else collide_u = "no"
			----------
		end
	end
	
	--check collision l/r
	if player.dx<0 then
		player.dx=limit_speed(player.dx,player.max_dx)
		if collide_map(player,"left","solid_colliders") then
			player.dx=0
			-- test --
			-- collide_l = "yes"
			-- else collide_l = "no"
			----------

			-- if not player.landed then
			-- 	player.sliding_y = true
			-- 	player.running = false
			-- 	player.jumping = false
			-- 	player.falling = false
			-- else player.sliding_y = false	end
		end
	elseif player.dx>0 then
		player.dx=limit_speed(player.dx,player.max_dx)
		if collide_map(player,"right","solid_colliders") then
			player.dx=0
			-- test --
			-- collide_r = "yes"
			-- else collide_r = "no"
			----------

			-- if not player.landed then
			-- 	player.sliding_y = true
			-- 	player.running = false
			-- 	player.jumping = false
			-- 	player.falling = false
			-- else player.sliding_y = false end
		end
	end
	
	--stop sliding_x
	if player.sliding_x then
		if math.abs(player.dx) < .2
		or player.running then
			player.dx=0
			player.sliding_x=false
		end
	end

	if player.sliding_y then

	end
	
	player.x = math.floor(player.x + player.dx)
	player.y = math.floor(player.y + player.dy)
end

function player_anim()
	if player.jumping then
		player.sp=7
	elseif player.falling then
		player.sp=8
	elseif player.sliding_x then
		player.sp=9
	elseif player.sliding_y then
		player.sp=10
	elseif player.running then
		if time()-player.anim>100 then
			player.anim=time()
			player.sp = player.sp+1
			if player.sp>6 then
				player.sp=3
			end
		end
	else
		if time()-player.anim>300 then
			player.anim=time()
			player.sp = player.sp+1
			if player.sp>2 then
				player.sp=1
			end
		end
	end
end

